mapper.select:
select
id, name, status, clean, maintenance, description, floor, serial, sort, price, pic
from hotel.rms_room


        and id = #{id}


        and name = #{name}


        and status = #{status}


        and clean = #{clean}


        and maintenance = #{maintenance}


        and description = #{description}


        and floor = #{floor}


        and serial = #{serial}


        and sort = #{sort}


        and price = #{price}


        and pic = #{pic}
